// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

uint256 constant ORDER_EXPECTED_TO_BE_VALID = 0;
uint256 constant NONCE_EXECUTED_OR_CANCELLED = 101;
uint256 constant NONCE_BELOW_MIN_ORDER_NONCE = 102;
uint256 constant ORDER_AMOUNT_CANNOT_BE_ZERO = 201;
uint256 constant MAKER_SIGNER_IS_NULL_SIGNER = 301;
uint256 constant INVALID_S_PARAMETER_EOA = 302;
uint256 constant INVALID_V_PARAMETER_EOA = 303;
uint256 constant NULL_SIGNER_EOA = 304;
uint256 constant WRONG_SIGNER_EOA = 305;
uint256 constant SIGNATURE_INVALID_EIP1271 = 311;
uint256 constant MISSING_IS_VALID_SIGNATURE_FUNCTION_EIP1271 = 312;
uint256 constant CURRENCY_NOT_WHITELISTED = 401;
uint256 constant STRATEGY_NOT_WHITELISTED = 402;
uint256 constant MIN_NET_RATIO_ABOVE_PROTOCOL_FEE = 501;
uint256 constant MIN_NET_RATIO_ABOVE_ROYALTY_FEE_REGISTRY_AND_PROTOCOL_FEE = 502;
uint256 constant MIN_NET_RATIO_ABOVE_ROYALTY_FEE_ERC2981_AND_PROTOCOL_FEE = 503;
uint256 constant MISSING_ROYALTY_INFO_FUNCTION_ERC2981 = 504;
uint256 constant TOO_EARLY_TO_EXECUTE_ORDER = 601;
uint256 constant TOO_LATE_TO_EXECUTE_ORDER = 602;
uint256 constant NO_TRANSFER_MANAGER_AVAILABLE_FOR_COLLECTION = 701;
uint256 constant CUSTOM_TRANSFER_MANAGER = 702;
uint256 constant ERC20_BALANCE_INFERIOR_TO_PRICE = 711;
uint256 constant ERC20_APPROVAL_INFERIOR_TO_PRICE = 712;
uint256 constant ERC721_TOKEN_ID_DOES_NOT_EXIST = 721;
uint256 constant ERC721_TOKEN_ID_NOT_IN_BALANCE = 722;
uint256 constant ERC721_NO_APPROVAL_FOR_ALL_OR_TOKEN_ID = 723;
uint256 constant ERC1155_BALANCE_OF_DOES_NOT_EXIST = 731;
uint256 constant ERC1155_BALANCE_OF_TOKEN_ID_INFERIOR_TO_AMOUNT = 732;
uint256 constant ERC1155_IS_APPROVED_FOR_ALL_DOES_NOT_EXIST = 733;
uint256 constant ERC1155_NO_APPROVAL_FOR_ALL = 734;

contracts/royaltyFeeHelpers/RoyaltyFeeRe.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import {IRoyaltyFeeRegistry} from "../interfaces/IRoyaltyFeeRegistry.sol";

/**
 * @title RoyaltyFeeRegistry
 * @notice It is a royalty fee registry for the DareMarket.
 */
contract RoyaltyFeeRegistry is
    Initializable,
    AccessControlUpgradeable,
    IRoyaltyFeeRegistry
{
    bytes32 public constant INTERACTABLE_ROLE = keccak256("INTERACTABLE_ROLE");

    struct FeeInfo {
        address setter;
        address receiver;
        uint256 fee;
    }

    // Limit (if enforced for fee royalty in percentage (10,000 = 100%)
    uint256 public royaltyFeeLimit;

    mapping(address => FeeInfo) private _royaltyFeeInfoCollection;

    event NewRoyaltyFeeLimit(uint256 royaltyFeeLimit);
    event RoyaltyFeeUpdate(
        address indexed collection,
        address indexed setter,
        address indexed receiver,
        uint256 fee
    );

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @notice Initialize
     * @param _royaltyFeeLimit new royalty fee limit (500 = 5%, 1,000 = 10%)
     */
    function initialize(address owner, uint256 _royaltyFeeLimit)
        public
        initializer
    {
        __Context_init_unchained();
        __AccessControl_init_unchained();

        address defaultAdmin = _msgSender();
        if (owner != address(0)) {
            defaultAdmin = owner;
        }

        _grantRole(DEFAULT_ADMIN_ROLE, defaultAdmin);

        require(_royaltyFeeLimit <= 9500, "Owner: Royalty fee limit too high");
        royaltyFeeLimit = _royaltyFeeLimit;
    }

    /**
     * @notice Update royalty info for collection
     * @param _royaltyFeeLimit new royalty fee limit (500 = 5%, 1,000 = 10%)
     */
    function updateRoyaltyFeeLimit(uint256 _royaltyFeeLimit)
        external
        override
        onlyRole(INTERACTABLE_ROLE)
    {
        require(_royaltyFeeLimit <= 9500, "Owner: Royalty fee limit too high");
        royaltyFeeLimit = _royaltyFeeLimit;

        emit NewRoyaltyFeeLimit(_royaltyFeeLimit);
    }

    /**
     * @notice Update royalty info for collection
     * @param collection address of the NFT contract
     * @param setter address that sets the receiver
     * @param receiver receiver for the royalty fee
     * @param fee fee (500 = 5%, 1,000 = 10%)
     */
    function updateRoyaltyInfoForCollection(
        address collection,
        address setter,
        address receiver,
        uint256 fee
    ) external override onlyRole(INTERACTABLE_ROLE) {
        require(fee <= royaltyFeeLimit, "Registry: Royalty fee too high");
        _royaltyFeeInfoCollection[collection] = FeeInfo({
            setter: setter,
            receiver: receiver,
            fee: fee
        });

        emit RoyaltyFeeUpdate(collection, setter, receiver, fee);
    }

    /**
     * @notice Calculate royalty info for a collection address and a sale gross amount
     * @param collection collection address
     * @param amount amount
     * @return receiver address and amount received by royalty recipient
     */
    function royaltyInfo(address collection, uint256 amount)
        external
        view
        override
        returns (address, uint256)
    {
        return (
            _royaltyFeeInfoCollection[collection].receiver,
            (amount * _royaltyFeeInfoCollection[collection].fee) / 10000
        );
    }

    /**
     * @notice View royalty info for a collection address
     * @param collection collection address
     */
    function royaltyFeeInfoCollection(address collection)
        external
        view
        override
        returns (
            address,
            address,
            uint256
        )
    {
        return (
            _royaltyFeeInfoCollection[collection].setter,
            _royaltyFeeInfoCollection[collection].receiver,
            _royaltyFeeInfoCollection[collection].fee
        );
    }
}

